import { useEffect, useState } from 'react';
import Paragraphe from '../composants/Paragraphe';
import image from '../ressources/background.jpg';
import './Chapitre.css'


function Chapitre(){
    return(
        <div class = " justify-content-center d-flex p-5">
            <div class = " col-xl-8 col-lg-10 col-12 p-5 mb-0 content-container" style={{backgroundColor :"#202730", borderRadius:20}}>
                <div className="col-12">
                    <img src={image} class="image"/>
                    
                </div>
            </div>
        </div>
    );
}

export default Chapitre;
