import { useEffect, useState } from 'react';
import './Navbar.css';
import Button from './Button.js';
import './Button.css';

function Navbar() {

    // Sélectionner l'élément li
  var liElement = document.getElementById('mon-li');

  // Ajouter un écouteur d'événement pour le clic sur l'élément li
  liElement.addEventListener('click', function() {
      // Vérifier si la classe li-active est déjà présente
      if (!liElement.classList.contains('li-active')) {
          // Ajouter la classe li-active à l'élément li
          liElement.classList.add('li-active');
      } else {
          // Retirer la classe li-active de l'élément li
          liElement.classList.remove('li-active');
      }
  });
    return(
        <>
          <nav class="navbar navbar-expand-lg m-3">
            <div class="container-fluid">
              <a class="navbar-brand" href="#">Aucune idée</a>
              <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                <span class="navbar-toggler-icon"></span>
              </button>
              <div class="collapse navbar-collapse" id="navbarSupportedContent">
                <ul class="navbar-nav ms-auto mb-2 mb-lg-0">
                  <li class="nav-item" id = "mon-li">
                    <a class="nav-link" aria-current="page" href="#">Accueil</a>
                  </li>
                  <li class="nav-item">
                    <a class="nav-link" href="#">Cours</a>
                  </li> 
                  <li class="nav-item">
                    <a class="nav-link" href="#">Exercice</a>
                  </li>
                  <li class="nav-item">
                    <a class="nav-link" href="#">Programme</a>
                  </li>
                  <li class="nav-item">
                    <a class="nav-link" href="#">Contrôle</a>
                  </li>
                  <li class="nav-item">
                    <a class="nav-link" href="#">Examen</a>
                  </li>
                </ul>
                <Button text = "Connexion" className ="connexion">Connexion</Button>
              </div>
            </div>
          </nav>
        </>
    )
}

export default Navbar;
