import { useEffect, useState } from 'react';
import SideBar from '../composants/SideBar';
import SearchBar from '../composants/SearchBar';
import Card from '../composants/Card';

function Cours() {
    const scrollableDivStyle = {
        maxHeight: '500px', // Hauteur maximale de la div
        overflowY: 'auto', // Défilement vertical automatique
        padding: '10px' // Optionnel : ajout de padding
    };
    return(
        <div className="container-fluid row column border h-100vh ">
            <div className=" column col-lg-4 col-md-6 border py-5">
                <SideBar></SideBar>
            </div>
            <div className='column col-lg-8 col-md-6 row border' style={scrollableDivStyle}>
                <SearchBar></SearchBar>
                <Card></Card>
                <Card></Card>
                <Card></Card>
                <Card></Card>
                <Card></Card>
                <Card></Card>
                <Card></Card>
                <Card></Card>
                
            </div>
        </div>   
    );
}

export default Cours;
